    //BOTÃO "CADASTRAR" (SEGUNDA TELA)
    private void jButton6MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton6MouseClicked
        //      generos.add(g1);

        Boolean loop = false;
        //WHILE PARA LOOPAR A MENSAGEM DA SENHA
        while (!loop) {

            String nomeCompleto = this.jTextField2.getText();
            String email = this.jTextField3.getText();
            int senha = Integer.parseInt(this.jTextField4.getText());
            int confirmaSenha = Integer.parseInt(this.jTextField5.getText());

            if (senha == confirmaSenha) {

                newUsuario.setEmail(email);
                newUsuario.setNome(nomeCompleto);
                newUsuario.setSenha(confirmaSenha);
                usuarios.add(newUsuario);
                cardLayout.show(pnlCards, "crdGeneros");
                loop = true;
            } else {
                JOptionPane.showMessageDialog(null, "Senhas diferentes. \nConfirme a senha.");
                break;
            }
        }
    }//GEN-LAST:event_jButton6MouseClicked

    //BOTÃO "FINALIZAR CADASTRO"
    private void jButton4MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton4MouseClicked

        this.buttons.add(this.jToggleButton1);
        this.buttons.add(this.jToggleButton2);
        this.buttons.add(this.jToggleButton3);
        this.buttons.add(this.jToggleButton4);
        this.buttons.add(this.jToggleButton5);
        this.buttons.add(this.jToggleButton6);
        this.buttons.add(this.jToggleButton7);
        this.buttons.add(this.jToggleButton8);
        this.buttons.add(this.jToggleButton9);
        this.buttons.add(this.jToggleButton10);

        generos.add(g1);
        generos.add(g2);
        generos.add(g3);
        generos.add(g4);
        generos.add(g5);
        generos.add(g6);
        generos.add(g7);
        generos.add(g8);
        generos.add(g9);
        generos.add(g10);

        //       String generosUsuario = null;
        if (!newUsuario.getNome().isEmpty()) {
            StringBuilder generosUsuarioBuilder = new StringBuilder();

            for (int i = 0; i < buttons.size(); i++) {
                if (buttons.get(i).isSelected()) {

                    for (Usuario u : usuarios) {
                        for (int a = 0; a < generos.size(); a++) {
                            if (a == i && generos.get(a) != null) {
                                u.adicionarGenerosInteresse(generos.get(a));
                                if (generosUsuarioBuilder.length() > 0) {
                                    generosUsuarioBuilder.append(", ");
                                }
                                generosUsuarioBuilder.append(generos.get(a).getNome());
                            }
                        }
                    }
                }
            }

            String generosUsuario = generosUsuarioBuilder.toString();

            JOptionPane.showMessageDialog(
                    null,
                    "Nome usuario: " + newUsuario.getNome() + "\nGeneros do usuario: " + generosUsuario
            );

            Object selectedUF = jComboBox2.getSelectedItem();

            if (selectedUF != null) {
                String estadoSelecionado = selectedUF.toString();
                JOptionPane.showMessageDialog(null, estadoSelecionado);
                // Agora, você pode usar 'estadoSelecionado' como desejar, por exemplo, adicionar ao usuário.
                newUsuario.setUf(estadoSelecionado);
            }
            String cidade = this.jTextField7.getText();
            newUsuario.setCidade(cidade);

            String celular = this.jTextField8.getText();
            newUsuario.setCelular(celular);

            //DAO STUFF
            UsuarioDao usuarioDao = DaoFactory.createUsuarioDao();
            usuarioDao.insert(newUsuario);

            //requisição de id do usuario
            Usuario ask = usuarioDao.findByEmailUSER(newUsuario.getEmail());
            //setar id ao objeto atual 
            newUsuario.setId(ask.getId());
            //agr sim adicionar generos interesse e relacionar usuario com generos
            usuarioDao.insertGenerosInteresse(newUsuario);

            usuarioDaoTotal = usuarioDao;

            cardLayout.show(pnlCards, "crdMenu");
            jPanel7.setVisible(false);

        }
    }//GEN-LAST:event_jButton4MouseClicked

