    //BOTÃO ATUALIZAR PERFIL
    private void jButton14MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton14MouseClicked
        //pegar dados da tela
        String cidade = this.jTextField11.getText();
        String celular = this.jTextField12.getText();
        String email = this.jTextField13.getText();
        String uf = (String) this.jComboBox3.getSelectedItem();

        newUsuario.setCidade(cidade);
        newUsuario.setEmail(email);
        newUsuario.setCelular(celular);
        newUsuario.setUf(uf);

        //verificar botoes, adicionar a generoInteresse, e add em genero_usuairo
        if (buttons.isEmpty()) {
            this.buttons.add(this.jToggleButton12);
            this.buttons.add(this.jToggleButton12);
            this.buttons.add(this.jToggleButton13);
            this.buttons.add(this.jToggleButton14);
            this.buttons.add(this.jToggleButton15);
            this.buttons.add(this.jToggleButton16);
            this.buttons.add(this.jToggleButton17);
            this.buttons.add(this.jToggleButton18);
            this.buttons.add(this.jToggleButton19);
            this.buttons.add(this.jToggleButton20);
        }

        if (generos.isEmpty()) {
            generos.add(g1);
            generos.add(g2);
            generos.add(g3);
            generos.add(g4);
            generos.add(g5);
            generos.add(g6);
            generos.add(g7);
            generos.add(g8);
            generos.add(g9);
            generos.add(g10);
        }

        if (!newUsuario.getGenerosInteresse().isEmpty()) {
            newUsuario.limparGenerosInteresse();
        }

        for (int i = 0; i < buttons.size(); i++) {
            if (buttons.get(i).isSelected()) {
                newUsuario.adicionarGenerosInteresse(generos.get(i));
            }
        }

        usuarioDaoTotal.updatePerfil(newUsuario);

        UsuarioDao usuarioDao = DaoFactory.createUsuarioDao();

        //requisição de id do usuario
        Usuario ask = usuarioDao.findByEmailUSER(newUsuario.getEmail());
        //setar id ao objeto atual 
        newUsuario.setId(ask.getId());

        usuarioDaoTotal.excluirGenerosInteresse(newUsuario.getId());
        usuarioDaoTotal.insertGenerosInteresse(newUsuario);
        JOptionPane.showMessageDialog(null, "Perfil Atualizado");
    }//GEN-LAST:event_jButton14MouseClicked

