    private void jButton20MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton20MousePressed
        if (generos.isEmpty()) {
            generos.add(g1);
            generos.add(g2);
            generos.add(g3);
            generos.add(g4);
            generos.add(g5);
            generos.add(g6);
            generos.add(g7);
            generos.add(g8);
            generos.add(g9);
            generos.add(g10);
        }

        estados.add(e1);
        estados.add(e2);
        estados.add(e3);

        String nomeLivro = this.jTextField14.getText();
        String autor = this.jTextField15.getText();
        String editora = this.jTextField16.getText();
        int qtdPaginas = Integer.parseInt(this.jTextField18.getText());
        int ano = Integer.parseInt(this.jTextField17.getText());

        Genero rapido = new Genero();
        String generoLivro = this.jComboBox1.getSelectedItem().toString();
        for (int i = 0; i < generos.size(); i++) {
            if (generos.get(i).getNome().equals(generoLivro)) {
                rapido = generos.get(i);
            }
        }

        Estado fast = new Estado();
        String estado = this.jComboBox5.getSelectedItem().toString();
        for (int i = 0; i < estados.size(); i++) {
            if (estados.get(i).getNome().equals(estado)) {
                fast = estados.get(i);
                break;
            }
        }

        Livro obj = new Livro(null, nomeLivro, ano, qtdPaginas, editora, autor, rapido, fast);
        LivroDao livroDao = DaoFactory.createLivroDao();

        // Verificar se o livro já existe no banco de dados
        if (!livroDao.livroExists(obj)) {
            livroDao.insert(obj);
            Livro temp = livroDao.findLivroByAllAttributes(obj.getNome(), obj.getAutor(), obj.getEditora(), obj.getAnoPubli(), obj.getQtdePaginas(), obj.getGenero(), obj.getEstado());
            livroDao.insertTrocas(newUsuario.getId(), temp.getId());
            JOptionPane.showMessageDialog(null, "Livro Cadastrado!");
        } else {
            Livro temp2 = livroDao.findLivroByAllAttributes(obj.getNome(), obj.getAutor(), obj.getEditora(), obj.getAnoPubli(), obj.getQtdePaginas(), obj.getGenero(), obj.getEstado());
            livroDao.insertTrocas(newUsuario.getId(), temp2.getId());
            livroDao.insertTrocas(newUsuario.getId(), temp2.getId());
            JOptionPane.showMessageDialog(null, "Livro Cadastrado!");
        }
    }//GEN-LAST:event_jButton20MousePressed

